@bot.message_handler(commands=['start'])
def start_message(message):
    markup = types.InlineKeyboardMarkup(row_width=1)
    family_button = types.InlineKeyboardButton("üë™–°–µ–º—å—èüë™", callback_data="ansfamily")
    friend_button = types.InlineKeyboardButton("üëä–î—Ä—É–∂–±–∞üëä", callback_data="ansfriend")
    love_button = types.InlineKeyboardButton("üíì–õ—é–±–æ–≤—åüíì", callback_data="anslove")
    money_button = types.InlineKeyboardButton("üíµ–î–µ–Ω—å–≥–∏üíµ", callback_data="ansmoney")
    war_button = types.InlineKeyboardButton("üí£–í–æ–π–Ω–∞üí£", callback_data="answar")
    peace_button = types.InlineKeyboardButton("üåè–ú–∏—Äüåè", callback_data="anspeace")
    time_button = types.InlineKeyboardButton("üïí–í—Ä–µ–º—èüïí", callback_data="anstime")

    markup.add(family_button, friend_button, love_button, money_button,war_button,peace_button,time_button)

    bot.send_message(message.chat.id, "–í—ã–±–µ—Ä–∏—Ç–µ:", reply_markup=markup)

@bot.callback_query_handler(func=lambda call: True)
def answer(callback):
    if callback.message:
        story_type = callback.data.replace("ans", "")
        if story_type in stories:
            story = random.choice(stories[story_type])
            bot.send_message(callback.message.chat.id, story)

